import React, { useState } from 'react';
import { Strategy, SimulationResult, PointCalculationInput } from '../types';
import { runSimulation, getDifficultyColor, getSuccessRateColor } from '../utils/simulator';
import {
  Container,
  Card,
  Title,
  Subtitle,
  Button,
  Input,
  Select,
  Label,
  FormGroup,
  Grid,
  Flex,
  Badge,
  ProgressBar,
  Alert
} from './styled/CommonStyles';

interface SimulatorProps {
  userPoints: number;
  userInput: PointCalculationInput;
}

const Simulator: React.FC<SimulatorProps> = ({ userPoints, userInput }) => {
  const [simulationResult, setSimulationResult] = useState<SimulationResult | null>(null);
  const [selectedStrategy, setSelectedStrategy] = useState<Strategy | null>(null);

  const handleRunSimulation = () => {
    const result = runSimulation(userPoints, userInput);
    setSimulationResult(result);
  };

  const getDifficultyText = (difficulty: string) => {
    switch (difficulty) {
      case 'easy': return '쉬움';
      case 'medium': return '보통';
      case 'hard': return '어려움';
      default: return '알 수 없음';
    }
  };

  const getCategoryText = (category: string) => {
    switch (category) {
      case 'public': return '공공분양';
      case 'private': return '민간분양';
      case 'special': return '특별공급';
      default: return '기타';
    }
  };

  const StrategyCard: React.FC<{ strategy: Strategy; type: 'recommended' | 'alternative' }> = ({ strategy, type }) => (
    <Card style={{ cursor: 'pointer' }} onClick={() => setSelectedStrategy(strategy)}>
      <Flex justify="space-between" align="center" style={{ marginBottom: '12px' }}>
        <h3 style={{ margin: 0 }}>{strategy.name}</h3>
        <Badge color={type === 'recommended' ? '#27ae60' : '#f39c12'}>
          {type === 'recommended' ? '추천' : '대안'}
        </Badge>
      </Flex>

      <p style={{ color: '#666', marginBottom: '16px' }}>{strategy.description}</p>

      <Grid columns={2} style={{ marginBottom: '16px' }}>
        <div>
          <Label>필요 가점</Label>
          <div>{strategy.minPoints}~{strategy.maxPoints}점</div>
        </div>
        <div>
          <Label>성공률</Label>
          <div style={{ color: getSuccessRateColor(strategy.successRate) }}>
            {strategy.successRate}%
          </div>
        </div>
      </Grid>

      <Flex justify="space-between" align="center">
        <Badge color={getDifficultyColor(strategy.difficulty)}>
          {getDifficultyText(strategy.difficulty)}
        </Badge>
        <Badge color="#3498db">
          {getCategoryText(strategy.category)}
        </Badge>
      </Flex>
    </Card>
  );

  return (
    <Container>
      <Title>청약 전략 시뮬레이터</Title>

      <Card>
        <Subtitle>시뮬레이션 정보</Subtitle>
        <Grid columns={2}>
          <div>
            <Label>현재 가점</Label>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#2c3e50' }}>
              {userPoints}점
            </div>
          </div>
          <div>
            <Label>선호 지역</Label>
            <div>{userInput.region}</div>
          </div>
          <div>
            <Label>가구 형태</Label>
            <div>
              {userInput.householdType === 'single' ? '1인 가구' : 
               userInput.householdType === 'couple' ? '부부' : '가족'}
            </div>
          </div>
          <div>
            <Label>연소득</Label>
            <div>{userInput.income}만원</div>
          </div>
        </Grid>

        <Flex justify="center" style={{ marginTop: '24px' }}>
          <Button onClick={handleRunSimulation} variant="primary">
            전략 시뮬레이션 실행
          </Button>
        </Flex>
      </Card>

      {simulationResult && (
        <>
          <Card>
            <Subtitle>시뮬레이션 결과</Subtitle>

            <Alert type={simulationResult.successProbability >= 70 ? 'success' : 
                        simulationResult.successProbability >= 50 ? 'warning' : 'error'}>
              <strong>전체 성공 확률: {simulationResult.successProbability.toFixed(1)}%</strong>
              {simulationResult.successProbability >= 70 ? ' - 높은 성공 가능성!' :
               simulationResult.successProbability >= 50 ? ' - 보통 성공 가능성' : ' - 낮은 성공 가능성'}
            </Alert>

            <ProgressBar 
              progress={simulationResult.successProbability} 
              color={getSuccessRateColor(simulationResult.successProbability)}
            />
          </Card>

          <Card>
            <Subtitle>추천 전략</Subtitle>
            {simulationResult.recommendedStrategies.length > 0 ? (
              <Grid columns={1}>
                {simulationResult.recommendedStrategies.map(strategy => (
                  <StrategyCard key={strategy.id} strategy={strategy} type="recommended" />
                ))}
              </Grid>
            ) : (
              <Alert type="warning">
                현재 가점으로 추천할 수 있는 전략이 없습니다. 대안 전략을 확인해보세요.
              </Alert>
            )}
          </Card>

          <Card>
            <Subtitle>대안 전략</Subtitle>
            {simulationResult.alternativeStrategies.length > 0 ? (
              <Grid columns={1}>
                {simulationResult.alternativeStrategies.map(strategy => (
                  <StrategyCard key={strategy.id} strategy={strategy} type="alternative" />
                ))}
              </Grid>
            ) : (
              <Alert type="info">
                대안 전략이 없습니다. 가점을 높이거나 다른 조건을 고려해보세요.
              </Alert>
            )}
          </Card>
        </>
      )}

      {selectedStrategy && (
        <Card>
          <Subtitle>전략 상세 정보</Subtitle>

          <Grid columns={2}>
            <div>
              <h3>{selectedStrategy.name}</h3>
              <p>{selectedStrategy.description}</p>

              <FormGroup>
                <Label>필요 가점 범위</Label>
                <div>{selectedStrategy.minPoints}~{selectedStrategy.maxPoints}점</div>
              </FormGroup>

              <FormGroup>
                <Label>예상 성공률</Label>
                <div style={{ color: getSuccessRateColor(selectedStrategy.successRate), fontSize: '1.2rem', fontWeight: 'bold' }}>
                  {selectedStrategy.successRate}%
                </div>
              </FormGroup>
            </div>

            <div>
              <FormGroup>
                <Label>적용 지역</Label>
                <div>{selectedStrategy.region}</div>
              </FormGroup>

              <FormGroup>
                <Label>가구 형태</Label>
                <div>
                  {selectedStrategy.householdType === 'single' ? '1인 가구' : 
                   selectedStrategy.householdType === 'couple' ? '부부' : '가족'}
                </div>
              </FormGroup>

              <FormGroup>
                <Label>분양 유형</Label>
                <div>{getCategoryText(selectedStrategy.category)}</div>
              </FormGroup>

              <FormGroup>
                <Label>난이도</Label>
                <Badge color={getDifficultyColor(selectedStrategy.difficulty)}>
                  {getDifficultyText(selectedStrategy.difficulty)}
                </Badge>
              </FormGroup>
            </div>
          </Grid>

          <Flex justify="center" style={{ marginTop: '24px' }}>
            <Button onClick={() => setSelectedStrategy(null)} variant="secondary">
              닫기
            </Button>
          </Flex>
        </Card>
      )}
    </Container>
  );
};

export default Simulator;
