import React from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';
import { Doughnut, Bar } from 'react-chartjs-2';
import { PointBreakdown } from '../types';
import { Card, Subtitle } from './styled/CommonStyles';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

interface PointChartProps {
  breakdown: PointBreakdown;
}

const PointChart: React.FC<PointChartProps> = ({ breakdown }) => {
  // 도넛 차트 데이터
  const doughnutData = {
    labels: ['나이', '무주택 기간', '부양가족 수', '청약통장', '소득'],
    datasets: [
      {
        data: [
          breakdown.agePoints,
          breakdown.homelessPoints,
          breakdown.dependentsPoints,
          breakdown.subscriptionAccountPoints,
          breakdown.incomePoints
        ],
        backgroundColor: [
          '#3498db',
          '#e74c3c',
          '#f39c12',
          '#27ae60',
          '#9b59b6'
        ],
        borderColor: [
          '#2980b9',
          '#c0392b',
          '#e67e22',
          '#229954',
          '#8e44ad'
        ],
        borderWidth: 2,
      },
    ],
  };

  // 바 차트 데이터
  const barData = {
    labels: ['나이', '무주택 기간', '부양가족 수', '청약통장', '소득'],
    datasets: [
      {
        label: '현재 점수',
        data: [
          breakdown.agePoints,
          breakdown.homelessPoints,
          breakdown.dependentsPoints,
          breakdown.subscriptionAccountPoints,
          breakdown.incomePoints
        ],
        backgroundColor: [
          '#3498db',
          '#e74c3c',
          '#f39c12',
          '#27ae60',
          '#9b59b6'
        ],
        borderColor: [
          '#2980b9',
          '#c0392b',
          '#e67e22',
          '#229954',
          '#8e44ad'
        ],
        borderWidth: 1,
      },
      {
        label: '최대 점수',
        data: [20, 30, 20, 20, 10],
        backgroundColor: 'rgba(189, 195, 199, 0.3)',
        borderColor: '#bdc3c7',
        borderWidth: 1,
        type: 'bar' as const,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'bottom' as const,
      },
      tooltip: {
        callbacks: {
          label: function(context: any) {
            return `${context.label}: ${context.parsed}점`;
          }
        }
      }
    },
  };

  const barOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'bottom' as const,
      },
      tooltip: {
        callbacks: {
          label: function(context: any) {
            return `${context.dataset.label}: ${context.parsed}점`;
          }
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        max: 30,
        ticks: {
          stepSize: 5
        }
      }
    }
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
      <Card>
        <Subtitle>가점 분포 (도넛 차트)</Subtitle>
        <Doughnut data={doughnutData} options={options} />
      </Card>

      <Card>
        <Subtitle>항목별 점수 비교 (바 차트)</Subtitle>
        <Bar data={barData} options={barOptions} />
      </Card>
    </div>
  );
};

export default PointChart;
